$(document).ready(function () {
    $(".button-animate1").click(function () {
        $(".img1")
            .animate({
                width: "300px",
                opacity: "1"
            })
            .animate({
                height: "300px",
                opacity: "1"
            })
            .animate({
                marginLeft: "110px",
                opacity: "1"
            })
            .animate({
                borderWidth: "10px",
                opacity: "1"
            })
            .animate({
                borderRadius: "150px",
                opacity: "1"
            })
    })
})

